    ;;
    deleteLecture)
    call_deleteLecture
    ;;
    deletePerson)
    call_deletePerson
    ;;
    deletePlanning)
    call_deletePlanning
    ;;
    getCourse)
    call_getCourse
    ;;
    getLecture)
    call_getLecture
    ;;
    getPerson)
    call_getPerson
    ;;
    getPlanning)
    call_getPlanning
    ;;
    listCourses)
    call_listCourses
    ;;
    listLectures)
    call_listLectures
    ;;
    listPersons)
    call_listPersons
    ;;
    listPlannings)
    call_listPlannings
    ;;
    replaceCourse)
    call_replaceCourse
    ;;
    replaceLecture)
    call_replaceLecture
    ;;
    replacePerson)
    call_replacePerson
    ;;
    replacePlanning)
    call_replacePlanning
    ;;
    sayHello)
    call_sayHello
    ;;
    *)
    ERROR_MSG="ERROR: Unknown operation: $operation"
    exit 1
esac
